descriptor.controllerScript:
self.onInit = function() {
    self.ctx.$scope.data = self.ctx.defaultSubscription
        .data;
    // trigger();
    // setTimeout(function() {
    //     trigger();
    // }, 1000);
    console.log("init", new Date())
};

self.onDataUpdated = function() {
    trigger()
    // console.log("update", new Date())
};

trigger = function() {
    var totalRuntime = 0;
    var totalInstance = 0;
    var rowsHtml = '';
    var wrappper = document.getElementsByClassName('wrappper')[0];
    wrappper.innerHTML = '';
    var elem = self.ctx.data;
    for(var i=0; i<self.ctx.data.length; i+=2) {
        // console.log(elem);

        var runtime, instance;
        elem[i].data.forEach(element => {
            runtime = element[1];
            totalRuntime += runtime;
        });
        elem[i+1].data.forEach(element => {
            instance = element[1];
            totalInstance += instance;
        });

        rowsHtml += `
            <tr>
                <td style="color:${elem[i].dataKey.color};">${elem[i].dataKey.label}</td>
                <td style="color:${elem[i].dataKey.color};">${format(runtime)}</td>
                <td style="color:${elem[i].dataKey.color};">${instance}</td>
            </tr>
        `;

    }


    wrappper.innerHTML += `
    <table>
        <thead>
            <tr>
                <th>Entity</th>
                <th>Runtime</th>
                <th>Run Instances</th>
            </tr>
        </thead>
        <tbody>
            ${rowsHtml}
        </tbody>
        <tfoot>
            <tr>
                <th>Total</th>
                <th>${format(totalRuntime)}</th>
                <th>${totalInstance}</th>
            </tr>
        </tfoot>
    </table>
`;
};

    format = function (value) {
            var hrs = parseInt(value/60);
            var min = value - (hrs*60);
            return hrs + " Hrs " + min + " Min";
    }

