.container {
    position: relative;
    width: 100%;
    height: 100%;
}

    .circle-container {
        position: absolute;
        text-align: center;
        display: flex;
        flex-direction: column;
        align-items: center;
        z-index: 10;
    }

    .circle {
        width: 100px;
        height: 100px;
        background-color: white;
        border-radius: 50%;
        border: 5px solid #bbbbbb;
        overflow: hidden;
        display: flex;
        justify-content: center;
        align-items: center;
        position: relative;
        z-index: 5;
    }

    .circle img {
        width: 80%;
        height: 80%;
        object-fit: cover;
    }

.value {
    font-size: 12px;
    margin: 3px 0;
}

.value.top {
    order: -1;
}

.label {
    font-size: 14px;
    margin-top: 5px;
}

.red {
    top: -1% ;
    left: 2%;
}

.red .circle {
    border-top: 5px solid #027507;
}

.yellow {
    bottom:0%;
    left: 19%;
}

.yellow .circle {
    border-top: 5px solid #048AD3;
}

.green {
    bottom: 0%;
    right: 12%;
}

.green .circle {
    border-top: 5px solid #C5E1A4;
}

.center {
    top: 30%;
    left: 50%;
    transform: translate(-50%, -50%);
}

.center .circle {
    border-top: 5px solid yellow;
}

.lines {
    position: absolute;
    width: 100%;
    height: 100%;
    z-index: 5;
}

line {
    stroke: #999;
    stroke-width: 2;
    stroke-linecap: round;
}
circle {
    r: 5;
}
#circle1, #circle2, #circle3 {
    box-shadow: 5px 0px 20px 5px #048AD3;
}

descriptor.controllerScript:
    
    
    self.onInit = function() {
      self.ctx.$scope.data = self.ctx.defaultSubscription.data;
        
    }
        
    self.onDataUpdated = function() {
        self.ctx.detectChanges();
        
        var sc1 = document.getElementById("circle1");
        var sc2 = document.getElementById("circle2");
        var sc3 = document.getElementById("circle3");
        console.log(sc2);
        var sum = 0;
        var arr = [];
        for(var i = 0; i < self.ctx.data.length; i++) {
            if(i!=2){
                self.ctx.data[i].data.forEach((elem) => {
                    sum += +elem[1];
                    arr.push(elem[1]);
                    if(i==0 && elem[1]<=0){
                        sc1.style.display = "none";
                    }
                    else if(i==0 && elem[1] > 0){
                        sc1.style.display = "block";
                        gsap.fromTo("#circle1", {
                            x:-145,
                            y:-40,
                        }, {
                            y: -40,
                            x: 0,
                            duration: 2,
                            repeat:-1,
                            ease: "power1.out"
                        });
                    }
                    if(i==1 && elem[1] <= 0){
                        sc2.style.display = "none";
                    }
                    else if(i==1 && elem[1] > 0){
                        sc2.style.display = "block";
                        gsap.fromTo("#circle2", {
                            x:-145,
                            y:55
                        }, {
                            y: 0,
                            x: 0,
                            repeat:-1,
                            duration: 2,
                            ease: "power1.out"
                        });
                    }
                    if(i==3 && elem[1] == 0){
                        sc3.style.display = "none";
                    }
                    else if(i==3 && elem[1] > 0){
                        sc3.style.display = "block";
                        sc3.style.fill = "#C5E1A4";
                        gsap.fromTo("#circle3",{
                            x:200,
                            y:0
                        }, {
                            y: 0,
                            x: 0,
                            repeat:-1,
                            duration: 2,
                            ease: "power1.out"
                        });
                    }
                    else if(i==3 && elem[1] < 0 && arr[0] > 0) {
                        sc3.style.display = "block";
                        sc3.style.fill = "#027507";
                        gsap.fromTo("#circle3",{
                            x:0,
                            y:0
                        }, {
                            y: 0,
                            x: 120,
                            repeat:-1,
                            duration: 2,
                            ease: "power1.out"
                        });
                    }
                    
                });
            }
        }
        console.log(sum)
        document.getElementById("sum").innerHTML = sum.toFixed(2) + " kW"

    }

