descriptor.controllerScript:
self.onInit = function() {
    var columnPower = ["TS"];
    var dataPower = [];
    for(var i=0; i < self.ctx.data.length ; i++){
        dataPower.push(self.ctx.data[i].data);
        columnPower.push(self.ctx.data[i].label);
    }

    var gridPower = $("div#report-power").Grid({
            pagination:false,
            fixedHeader: true,
            height: 'auto',
            resizable: true,
            columns : columnPower,
            data : [],
            className : {
                table : "table",
                tr : "tr",
                th : "th",
                td : "td"
            },

        });


};

self.onDataUpdated = function() {

    var columnPower = ["Date & Time"];
    var dataPower = [];
    for(var i=0; i < self.ctx.data.length ; i++){
        columnPower.push(self.ctx.data[i].dataKey.label);
        if (i==0) {
            dataPower.push(self.ctx.data[i].data.map(elem => new Date(elem[0]).toLocaleString()));
            dataPower.push(self.ctx.data[i].data.map(elem => (elem[1]).toFixed(2)));
        }else{
            dataPower.push(self.ctx.data[i].data.map(elem => (elem[1]).toFixed(2)));
        }
    }

        var gridDataPower = [];
    if (dataPower[0].length > 0) {

        for (var k = (dataPower[0].length - 1); k >= 0; k--) {
            var tempArrayPower = [];
            for (var j = 0; j < dataPower.length; j++) {
                tempArrayPower.push(dataPower[j][k]);
            }
            gridDataPower.push(tempArrayPower);
        }
    }
    console.log(gridDataPower);

    // Prepare the updated data for the grid
    $("div#report-power").empty();
    var gridPower = $("div#report-power").Grid({
            pagination: {
                limit : 10,
                summary: false,
                resetPageOnUpdate: false
            },
            fixedHeader: true,
            resizable: true,
            // height: "600px",
        columns : columnPower,
        data : gridDataPower,
        className : {
            container: "report2",
            table : "table",
            tr : "tr",
            th : "th",
            td : "td"
        },
    });
    gridPower.forceRender();

downloadPDFPower = function() {
    var element = document.getElementById('report-power'); 
    html2pdf(element);
}

downloadCSVPower = function() {
    var csvDataPower = []
    // Add column names as the first row
    csvDataPower.push(columnPower);

    // Add grid data rows
    csvDataPower = csvDataPower.concat(gridDataPower);

    var csv = Papa.unparse(csvDataPower);

        // Create a Blob
        var blob = new Blob([csv], { type: 'text/csv;charset=utf-8;' });

        // Trigger the download
        var downloadLink = document.createElement('a');
        downloadLink.href = window.URL.createObjectURL(blob);
        downloadLink.setAttribute('download', `PowerReport_${new Date().getDate()}-${new Date().getMonth()+1}-${new Date().getFullYear()}.csv`);
        downloadLink.click();
    }
};

