descriptor.controllerScript:
self.onInit = function() {
    var columnInverter = ["TS"];
    var dataInverter = [];
    for(var i=0; i < self.ctx.data.length ; i++){
        dataInverter.push(self.ctx.data[i].data);
        columnInverter.push(self.ctx.data[i].label);
    }

    var gridInverter = $("div#report-inverter").Grid({
            pagination:false,
            fixedHeader: true,
            height: 'auto',
            resizable: true,
            columns : columnInverter,
            data : [],
            className : {
                table : "table",
                tr : "tr",
                th : "th",
                td : "td"
            },

        });


};

self.onDataUpdated = function() {

    var columnInverter = ["Date & Time"];
    var dataInverter = [];
    for(var i=0; i < self.ctx.data.length ; i++){
        columnInverter.push(self.ctx.data[i].dataKey.label);
        if (i==0) {
            dataInverter.push(self.ctx.data[i].data.map(elem => new Date(elem[0]).toLocaleString()));
            dataInverter.push(self.ctx.data[i].data.map(elem => (elem[1]).toFixed(2)));
        }else{
            dataInverter.push(self.ctx.data[i].data.map(elem => (elem[1]).toFixed(2)));
        }
    }

        var gridDataInverter = [];
    if (dataInverter[0].length > 0) {

        for (var k = (dataInverter[0].length - 1); k >= 0; k--) {
            var tempArrayInverter = [];
            for (var j = 0; j < dataInverter.length; j++) {
                tempArrayInverter.push(dataInverter[j][k]);
            }
            gridDataInverter.push(tempArrayInverter);
        }
    }

    // Prepare the updated data for the grid
    $("div#report-inverter").empty();
    var gridInverter = $("div#report-inverter").Grid({
            pagination: {
                limit : 10,
                summary: false,
                resetPageOnUpdate: false
            },
            fixedHeader: true,
            resizable: true,
            // height: "600px",
        columns : columnInverter,
        data : gridDataInverter,
        className : {
            container: "report2",
            table : "table",
            tr : "tr",
            th : "th",
            td : "td"
        },
    });
    gridInverter.forceRender();

downloadPDF = function() {
    var element = document.getElementById('report-inverter'); 
    html2pdf(element);
}

downloadCSVInverter = function() {
    var csvDataInverter = []
    // Add column names as the first row
    csvDataInverter.push(columnInverter);

    // Add grid data rows
    csvDataInverter = csvDataInverter.concat(gridDataInverter);

    var csvInverter = Papa.unparse(csvDataInverter);

        // Create a Blob
        var blobInverter = new Blob([csvInverter], { type: 'text/csv;charset=utf-8;' });

        // Trigger the download
        var downloadLinkInverter = document.createElement('a');
        downloadLinkInverter.href = window.URL.createObjectURL(blobInverter);
        downloadLinkInverter.setAttribute('download', `Inverter&DGReport_${new Date().getDate()}-${new Date().getMonth()+1}-${new Date().getFullYear()}.csv`);
        downloadLinkInverter.click();
    }
};

