self.onInit = function() {
    var columnDGRuntime = ["TS"];
    var dataDGRuntime = [];
    for(var i=0; i < self.ctx.data.length ; i++){
        dataDGRuntime.push(self.ctx.data[i].data);
        columnDGRuntime.push(self.ctx.data[i].label);
    }

    var gridDGRuntime = $("div#report-DGRuntime").Grid({
            pagination:false,
            fixedHeader: true,
            height: 'auto',
            resizable: true,
            columns : columnDGRuntime,
            data : [],
            className : {
                table : "table",
                tr : "tr",
                th : "th",
                td : "td"
            },

        });


};

self.onDataUpdated = function() {
    // Get current date
            var currentDate = new Date();

            // Subtract one day to get previous day's date
            var previousDate = new Date(currentDate);
            previousDate.setDate(previousDate.getDate() - 1);

            // Set end time to 11:59:59 pm
            var endTime = new Date(previousDate);
            endTime.setHours(23, 59, 0, 999);

            // Convert to timestamps (in milliseconds)
            var endTimestamp = endTime.getTime();
            var push = new Date(endTimestamp).toLocaleString();

    var columnDGRuntime = ["Date & Time"];
    var dataDGRuntime = [];
    for(var i=0; i < self.ctx.data.length ; i++){
        columnDGRuntime.push(self.ctx.data[i].dataKey.label);
        if (i==0) {
            // console.log(endTimestamp)
            // dataDGRuntime.push(endTimestamp)
            dataDGRuntime.push(self.ctx.data[i].data.map(elem => push));
            dataDGRuntime.push(self.ctx.data[i].data.map(elem => (elem[1]).toFixed(2)));
        }
        else{
            dataDGRuntime.push(self.ctx.data[i].data.map(elem => (elem[1]).toFixed(2)));
        }
    }
        var gridDataDGRuntime = [];
        var valueData = [];
    if (dataDGRuntime[0].length > 0) {

        for (var k = (dataDGRuntime[0].length - 1); k >= 0; k--) {
            var tempArrayDGRuntime = [];
            for (var j = 0; j < dataDGRuntime.length; j++) {
                tempArrayDGRuntime.push(dataDGRuntime[j][k]);
            }
            gridDataDGRuntime.push(tempArrayDGRuntime);
        }
        valueData = [gridDataDGRuntime[0]];
    }
    console.log(valueData);
    // Prepare the updated data for the grid
    $("div#report-DGRuntime").empty();
    var gridDGRuntime = $("div#report-DGRuntime").Grid({
            pagination: {
                limit : 10,
                summary: false,
                resetPageOnUpdate: false
            },
            fixedHeader: true,
            resizable: true,
            // height: "600px",
        columns : columnDGRuntime,
        data : valueData,
        className : {
            container: "report3",
            table : "table",
            tr : "tr",
            th : "th",
            td : "td"
        },
    });
    gridDGRuntime.forceRender();

downloadPDF = function() {
    var element = document.getElementById('report-DGRuntime'); 
    html2pdf(element);
}

downloadCSVDGRuntime = function() {
    var csvDataDGRuntime = []
    // Add column names as the first row
    csvDataDGRuntime.push(columnDGRuntime);

    // Add grid data rows
    csvDataDGRuntime = csvDataDGRuntime.concat(gridDataDGRuntime);

    var csvDGRuntime = Papa.unparse(csvDataDGRuntime);

        // Create a Blob
        var blobDGRuntime = new Blob([csvDGRuntime], { type: 'text/csv;charset=utf-8;' });

        // Trigger the download
        var downloadLinkDGRuntime = document.createElement('a');
        downloadLinkDGRuntime.href = window.URL.createObjectURL(blobDGRuntime);
        downloadLinkDGRuntime.setAttribute('download', `DG_Runtime_${new Date().getDate()}-${new Date().getMonth()+1}-${new Date().getFullYear()}.csv`);
        downloadLinkDGRuntime.click();
    }
};

