descriptor.controllerScript:
    
    
    self.onInit = function() {
       self.ctx.$scope.data = self.ctx.defaultSubscription.data;
        
        var Dbid = window.location.href;
        var z = Dbid.split('/');
        z.reverse();
        console.log("z", z[0].substring(0,36), z);
        var id = z[0].substring(0,36);
            
            dailyDG();
            DGAPI('month', 0, id);
            DGAPI('year', 1, id);
            
        // const regex = /(?:dashboard|dashboards\/all)\/([a-f0-9-]+)/;
        // const match = Dbid.match(regex);
        
        // if (match) {
        //   const id = match[1]; // Outputs: f23698d0-1e86-11ef-982c-0f5367becb87
        //   console.log(id);
        
        // } else {
        //   console.log("ID not found");
        // }
       
    }
        
    self.onDataUpdated = function() {
        self.ctx.detectChanges();
    }
    
      dailyDG = function(){
        document.getElementById("dailyDG").classList.add("active");
        document.getElementById("monthlyDG").classList.remove("active");
        document.getElementById("yearlyDG").classList.remove("active");
        
        document.getElementsByClassName('data-table-DG')[0].style.display = "none";
        document.getElementsByClassName('data-table-DG')[1].style.display = "none";
        document.getElementsByClassName('data-containerDG')[0].style.display = "flex";
        // self.ctx.timewindowFunctions.onResetTimewindow()
        
        self.ctx.timewindowFunctions.onResetTimewindow();
        
    }
    
    monthlyDG = function() {
        // self.ctx.timewindowFunctions.onResetTimewindow();
        document.getElementById("dailyDG").classList.remove("active");
        document.getElementById("monthlyDG").classList.add("active");
        document.getElementById("yearlyDG").classList.remove("active");
        
        document.getElementsByClassName('data-containerDG')[0].style.display = "none";
        document.getElementsByClassName('data-table-DG')[1].style.display = "none";
        document.getElementsByClassName('data-table-DG')[0].style.display = "block";
    }
    yearlyDG = function() {
        // self.ctx.timewindowFunctions.onResetTimewindow();
        document.getElementById("dailyDG").classList.remove("active");
        document.getElementById("monthlyDG").classList.remove("active");
        document.getElementById("yearlyDG").classList.add("active");
        
        document.getElementsByClassName('data-containerDG')[0].style.display = "none";
        document.getElementsByClassName('data-table-DG')[0].style.display = "none";
        document.getElementsByClassName('data-table-DG')[1].style.display = "block";

    }
    
    DGAPI = function (diff, no, id) {
      
      var rows = " ";
        var update = document.getElementsByClassName('data-table-DG')[no];
            update.innerHTML = " ";
        
        var label = [];
        var keys = "";
        var dashboardId = id;
        var customer = self.ctx.currentUser.customerId;
        var dif = diff;
        
        self.ctx.data.forEach(elem => {
            label.push(elem.dataKey.label, elem.dataKey.name);
            keys = keys + ",'" + elem.dataKey.name+"'";
        });
            
        // console.log(keys, label);
            
        var query = `SELECT * FROM fn_get_DGCard ('${dashboardId}','${customer}','${dif}' ${keys})`; 
        var url = "https://api.cogneta.cloud/api/sql/"+query;
        
        fetch(encodeURI(url))
        .then(res => res.json())
        .then(result => {
                var result_set = mapKeysToLabelsArrayDG(result.rows, label);
                DGHtmlRow(result_set, label, update);
            }) 
        .catch(console.error);  
    };
    
    DGHtmlRow = function (result_set, label, update) {
        
        // console.log(result_set[0], result_set, update);
        
        for (var i = 0; i < result_set.length; i += 2) {
            var rows = '<div class="data-rowDG"><div class="data-keyDG">' + result_set[i] + ':</div><div class="data-valueDG">' + result_set[i + 1] + '</div></div>';
            update.innerHTML += rows;
        }
    };
    
    mapKeysToLabelsArrayDG = function (data, keysAndLabels) {
        var result = [];

        // Create a mapping of keys to labels
        var keyLabelMap = {};
        for (var i = 0; i < keysAndLabels.length; i += 2) {
            keyLabelMap[keysAndLabels[i + 1]] = keysAndLabels[i];
        }
        
        // Map each key in the data to its corresponding label and value
        data.forEach(item => {
            var label = keyLabelMap[item.key_1];
            if (label) {
                result.push(label);
                result.push(item.result_set);
            }
        });
    
        return result;
    };

