var myChart;

self.onInit = function() {
    
    var chartData = {
        series: []
    };

    for (var i=0; i < self.ctx.data.length; i++) {
        var dataKey = self.ctx.data[i].dataKey;
        var series = {
            name: dataKey.label,
            data: [],
            color: dataKey.color
        };
        chartData.series.push(series);
    }
    
    var options = {
        chart: {
            type: 'column',
            height: '45%', // Set initial height to 50%
            events: {
                load: function () {
                    this.reflow(); // Trigger reflow on chart load
                },
            //     redraw: function () {
            //     var chart = this,
            //         containerWidth = chart.renderTo.offsetWidth,
            //         containerHeight = chart.renderTo.offsetHeight;

            //     chart.setSize(containerWidth, containerHeight, false);
            // }
            },
            renderTo: 'br1',
        },
        title: false,
        //     text: 'Active Power' 
        // },
        tooltip: {
            shared : true
        },
        xAxis: {
            type: 'datetime',
            tickPixelInterval: 150,
        },
        yAxis: {
            title: {
                text: 'kWh'
            },
            labels: {
                style: {
                    letterSpacing: '1px'
                }
            }
        },
        legend: {
            enabled: true,
            itemStyle : {
                fontSize : "10px"
            }
        },
        plotOptions: {
            // series: {
            //     stacking:'normal',
            //     dataLabels: {
            //         enabled: true,
            //         crop: false,
            //         overflow: 'hidden',
                    // formatter: function() {
                    //     return Highcharts.numberFormat(this.y, 2);
                    //     // return this.series.name + ': ' + Highcharts.numberFormat(this.y, 2);
                    // },
            //     },
            //     labels: {
            //         enabled: true
            //     }
            // },
            // bar: {
            //     pointWidth: 30,
            //     borderRadius: 5,
            //     dataLabels: {
            //         enabled: true,
            //         style: {
            //             fontSize: '8px'
            //         },
            //         formatter: function() {
            //             // return Highcharts.numberFormat(this.y, 2);
            //             return this.series.name + ': ' + Highcharts.numberFormat(this.y, 2);
            //         },
            //     },
            // },
            column: {
                pointPadding: 0.1, // Adjust this value to separate the bars more
                groupPadding: 0.1, // Adjust this value to separate the groups more
                pointWidth:15,
                // grouping: false,
                pointPlacement: 'between'
            },
            
        },
        exporting: {
            buttons: {
                contextButton: {
                    menuItems: ['downloadCSV', 'downloadPNG'] // Specify CSV and PNG download options
                }
            }
        },
    credits: {
            enabled: false // Remove Highcharts logo
        }
    };
    
    myChart = new Highcharts.Chart(options);
    self.onResize();
}

self.onResize = function() {
        // // height: '45%'
        // myChart.reflow();
        if (myChart) {
        var chartContainer = document.getElementById('br1'); 
        var containerWidth = chartContainer.offsetWidth;
        var containerHeight = chartContainer.offsetHeight;
        myChart.setSize(containerWidth, '45%', false);
        // this.reflow();
        
    }
}

self.onDataUpdated = function() {
    for (var i = 0; i < self.ctx.data.length; i++) {
        var datasourceData = self.ctx.data[i];
        var dataSet = datasourceData.data;
        var series = myChart.series[i];
        if (!series) {
            series = myChart.addSeries({
                name: datasourceData.dataKey.label,
                data: [],
                color: self.ctx.data[i].dataKey.color
            }, false);
        }
        var data = dataSet.map(function(tsValuePair) {
            return { x: tsValuePair[0], y: tsValuePair[1] };
        });
        series.setData(data, true);
    }
}

